sysauto_script.script:
try { 
 var r = new sn_ws.RESTMessageV2('x_425618_integrati.CoinMarketCap', 'Default GET');
 r.setStringParameterNoEscape('api_key', '2a3e75e8-f759-434c-a5f3-0b55c1274df0');

 var response = r.execute();
 var responseBody = response.getBody();
 var httpStatus = response.getStatusCode();
 this.processData(responseBody);
}
catch(ex) {
 var message = ex.message;
}


function processData(body) {
	//Setup Import
	var impSet = new GlideRecord('sys_import_set');
	impSet.state = 'loading';
	impSet.mode = 'asynchronous';
	impSet.table_name = 'x_425618_integrati_crypto_import_set_table';
	impSet.short_description = 'Scheduled Import';
	impSet.insert();

	//Parse Data
	var reponse = JSON.parse(body);

	// For every crypto object
	for (var i = 0; i < body.data.length; i++){
		// Crypto object
		var currency = body.data[i];
		var impSetRow = new GlideRecord('x_425618_integrati_crypto_import_set_table');
		impSetRow.sys_import_state = 'pending';
		impSetRow.sys_import_set = impSet.getUniqueValue();

		// Assign property values from crypto obj to value in import set row.
		impSetRow.name = currency.name;
		impSetRow.symbol = currency.symbol;
		impSetRow.price = parseFloat(currency.quote.USD.price);
		impSetRow.id = parseInt(currency.Id);
		impSetRow.insert();
	}

	impSet.state = 'loaded';
	impSet.update();

	// Calls and runs correct transform map?
	new GlideImportSetTransformer().transformAllMaps(impSet);
}
